package ar.com.wolox.android.example.ui.training.homePage.news

import android.support.v7.widget.LinearLayoutManager
import android.support.v7.widget.RecyclerView
import android.widget.Toast
import ar.com.wolox.android.R
import ar.com.wolox.android.example.model.News
import ar.com.wolox.wolmo.core.fragment.WolmoFragment
import ar.com.wolox.wolmo.core.presenter.BasePresenter
import kotlinx.android.synthetic.main.news_fragment_page.*
import javax.inject.Inject

/**
 * NewsFragment is used for display news
 */
class NewsFragment @Inject constructor() : WolmoFragment<BasePresenter<Any>>() {

    private lateinit var mRecyclerView: RecyclerView
    private lateinit var viewAdapter: RecyclerView.Adapter<*>
    private lateinit var viewManager: RecyclerView.LayoutManager
    private lateinit var news: MutableList<News>
    private var count: Int = 3

    override fun init() {
        val news = mutableListOf<News>()

        val fakeNews = News("Hola", "\"An ex-counterintelligence agent who defected to Iran in 2013 helped the Islamic Republic in \\\"targeting her former fellow agents\\\" and exposed a Defense Department program considered one of the crown jewels of U.S. intelligence, according to an explosive indictment unsealed", "data:image/jpeg;base64,")
        val fakeNews1 = News("Bootstrap", "\"An ex-counterintelligence agent who defected to Iran in 2013 helped the Islamic Republic in \\\"targeting her former fellow agents\\\" and exposed a Defense Department program considered one of the crown jewels of U.S. intelligence, according to an explosive indictment unsealed", "data:image/jpeg;base64,")
        val fakeNews2 = News("Wolox", "\"An ex-counterintelligence agent who defected to Iran in 2013 helped the Islamic Republic in \\\"targeting her former fellow agents\\\" and exposed a Defense Department program considered one of the crown jewels of U.S. intelligence, according to an explosive indictment unsealed", "data:image/jpeg;base64,")
        val fakeNews3 = News("Android", "\"An ex-counterintelligence agent who defected to Iran in 2013 helped the Islamic Republic in \\\"targeting her former fellow agents\\\" and exposed a Defense Department program considered one of the crown jewels of U.S. intelligence, according to an explosive indictment unsealed", "data:image/jpeg;base64,")
        news.add(fakeNews)
        news.add(fakeNews1)
        news.add(fakeNews2)
        news.add(fakeNews3)

        viewManager = LinearLayoutManager(context)
        viewAdapter = RecyclerAdapter(news)
        vSwipeRefreshLayout.setOnRefreshListener {
            if (count>0) {
                news.addAll(news)
                viewAdapter.notifyDataSetChanged()
                count--
            } else {
                Toast.makeText(context, getString(R.string.no_news), Toast.LENGTH_LONG).show()
            }
            vSwipeRefreshLayout.setRefreshing(false)
        }

        mRecyclerView = news_recycler.apply {
            setHasFixedSize(true)

            // use a linear layout manager
            layoutManager = viewManager

            // specify an viewAdapter (see also next example)
            adapter = viewAdapter
        }
    }

    override fun layout(): Int = R.layout.news_fragment_page
}